import React from "react";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import tabList from "./data/tabs.json";
import AddBoxTwoToneIcon from "@material-ui/icons/AddBoxTwoTone";
import CloseIcon from "@material-ui/icons/Close";
import { makeStyles, Theme } from "@material-ui/core/styles";

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    flexGrow: 1,

    borderTop: "solid",
    borderTopWidth: "thin",
  },
  bg: {
    backgroundColor: "white",
    color: "black",
  },
  menu: {
    color: "black",
    marginTop: "12px",
    marginLeft: "5px",
  },
  tab: {
    backgroundColor: "red",
  },
  add: {
    color: "black",
    marginLeft: "60px",
    marginTop: "12px",
  },
  panel: {
    backgroundColor: "white",
  },
  spread: {
    width: "100%",
  },
  bg1: {
    marginLeft: "128px",
    marginBottom: "-20px !important",
    backgroundColor: "white",
    color: "black",
    fontSize: "15px",
  },
}));

interface tabProps {
  children?: React.ReactNode;
  index: any;
  value: any;
}

interface tab {
  id: number;
  value: number;
  tabLabel: String;
}
function TabPanel(props: tabProps) {
  const { children, value, index, ...other } = props;
  const classes = useStyles();
  return (
    <div
      className={classes.panel}
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index: any) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

export default function SimpleTabs() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);
  const [tabs, settabs] = React.useState<tab[]>(tabList);
  const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <AppBar>
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="simple tabs example"
        >
          {tabs.map((tab) => (
            <Tab
              label={tab.tabLabel}
              icon={<CloseIcon onClick={() => removeTab(tab.id)} />}
            ></Tab>
          ))}
        </Tabs>
        <AddBoxTwoToneIcon onClick={addTab} />
      </AppBar>

      <TabPanel value={value} key={value} index={value}>
        <div></div>
      </TabPanel>
    </div>
  );

  function removeTab(ID: number) {
    const newList = tabs.filter((tab) => tab.id !== ID);
    settabs(newList);
  }
  function addTab() {
    settabs([
      ...tabs,
      { id: tabs.length + 1, value: tabs.length + 1, tabLabel: "NEW TAB" },
    ]);
  }
}
